'use strict';

var loggy = require('loggy');

var initStarter = require('./init-starter');

module.exports = function (rootPath) {
  var starter = arguments.length <= 1 || arguments[1] === undefined ? 'gh:gatsbyjs/gatsby-starter-default' : arguments[1];

  initStarter(starter, {
    rootPath: rootPath,
    logger: loggy
  }, function (error) {
    if (error) {
      loggy.error(error);
    }
  });
};